import{_ as t,o as r,c as a,a as o,t as i,d as l}from"./app.9ef8fb67.js";const s="/Linux-Blog/assets/terminal-parts.9e179ba8.png",b=JSON.parse('{"title":"Terminal for Children","description":"","frontmatter":{"title":"Terminal for Children","slug":"Terminal"},"headers":[],"relativePath":"guide/Terminal.md"}'),n={name:"guide/Terminal.md"},c=l('<div class="info custom-block"><p class="custom-block-title"><p style="text-align:center;">Lets kill the monster called terminal</p></p><p><span style="color:var(--vp-c-brand);"><em>Terminal is a different way to do stuff on your computer.</em></span></p></div><h1 id="terminal-and-it-parts" tabindex="-1">Terminal and it parts <a class="header-anchor" href="#terminal-and-it-parts" aria-hidden="true">#</a></h1><p>::: box <img src="'+s+'" style="margin:auto;height:300px;border-radius:10px;"> :::</p><p><strong>Terminal</strong> -&gt; The window that contains everything in the image is the terminal (under the purple border).</p><div class="tip custom-block"><p class="custom-block-title">Terminal</p><ol><li>It decides the fonts, color-schemes &amp; all the other visual aspects which you see when running the terminal</li><li>Good recommendation for general terminals on linux : <br><a href="https://github.com/alacritty/alacritty" target="_blank" rel="noreferrer">Alacritty</a> -&gt; Minimal, small size <br><a href="https://github.com/kovidgoyal/kitty" target="_blank" rel="noreferrer">Kitty</a> -&gt; Featureful, Extensible with plugins ( named: kittens \u{1F431} )</li></ol></div><p><strong>Prompt</strong> -&gt; The &quot;~/Desktop&quot; you see before the cursor is called the prompt. (It can get pretty fancy with a lot of useful infomation in it)</p><div class="tip custom-block"><p class="custom-block-title">Prompt</p><ol><li>Gives useful infomation like time, path of the current directory, git infomation and language version.</li><li>Good recommendation for general prompts on linux : <br><a href="https://starship.rs/guide/#%F0%9F%9A%80-installation" target="_blank" rel="noreferrer">starship</a> -&gt; Featureful, Provide information about git and all the language version.</li></ol></div><p><strong>Shell</strong> -&gt; It is the place where you type your command. It is also the layer between user and system.<br> Press <code>&lt;TAB&gt;</code> to get autosuggestions.<br> You can check the shell you are running with <code>echo $SHELL</code>. Your terminal should be either one of the following - bash,zsh or dash.</p><div class="tip custom-block"><p class="custom-block-title">Shell</p><ol><li>Provides helpful suggestion about command and there flags</li><li>Good recommendation for general prompts on linux : <br><a href="https://fishshell.com/docs/current/tutorial.html" target="_blank" rel="noreferrer">fish</a> -&gt; Featureful, Provide information about git and all the language version.</li></ol></div><details class="details custom-block"><summary>Good resources</summary><p>Kitty -&gt; <a href="https://paul-nameless.com/mastering-kitty.html" target="_blank" rel="noreferrer">Mastering Kitty</a></p></details>',10);function m(e,d,h,p,u,g){return r(),a("div",null,[o("h1",null,i(e.$frontmatter.title),1),c])}const _=t(n,[["render",m]]);export{b as __pageData,_ as default};
